import { systemPrompt } from './system';
import { personaPrompt } from './persona';
import { guidelinePrompt } from './guideline';

export const combineSystemPrompts = () => {
  return `${systemPrompt}\n\n${personaPrompt}\n\n${guidelinePrompt}`;
};
