'use client'

import React from 'react'
import styled from 'styled-components'
import Icon from '@/components/ai-esti/Icon'
import { IoChevronForward } from 'react-icons/io5'

const Container = styled.div`
  min-height: 100vh;
  background-color: ${({ theme }) => theme.body};
  padding: 20px 16px;
`

const ProfileSection = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
  padding: 16px 0;
  margin-bottom: 24px;
  cursor: pointer;
`

const ProfileImage = styled.div`
  width: 56px;
  height: 56px;
  border-radius: 50%;
  overflow: hidden;
`

const ProfileInfo = styled.div`
  flex: 1;
`

const ProfileName = styled.div`
  font-size: 18px;
  font-weight: 600;
  color: ${({ theme }) => theme.text};
  margin-bottom: 4px;
`

const ProfileEmail = styled.div`
  font-size: 14px;
  color: ${({ theme }) => theme.subtleText};
`

const ChevronIcon = styled(IoChevronForward)`
  color: ${({ theme }) => theme.subtleText};
  width: 24px;
  height: 24px;
`

const Section = styled.div`
  margin-bottom: 32px;
`

const SectionTitle = styled.h2`
  font-size: 16px;
  font-weight: 600;
  color: ${({ theme }) => theme.text};
  margin-bottom: 16px;
`

const MenuItem = styled.div`
  display: flex;
  align-items: center;
  padding: 16px;
  background-color: ${({ theme }) => theme.surface1};
  border-radius: 12px;
  cursor: pointer;
  margin-bottom: 12px;

  &:hover {
    background-color: ${({ theme }) => theme.surface2};
  }
`

const MenuText = styled.span`
  flex: 1;
  font-size: 16px;
  color: ${({ theme }) => theme.text};
`

const LanguageSection = styled.div`
  display: flex;
  gap: 12px;
  margin-top: 16px;
`

const LanguageButton = styled.button<{ $isSelected: boolean }>`
  flex: 1;
  padding: 12px;
  border-radius: 12px;
  border: 1px solid ${({ theme, $isSelected }) => 
    $isSelected ? theme.accent : theme.border};
  background-color: ${({ theme, $isSelected }) => 
    $isSelected ? theme.accent : 'transparent'};
  color: ${({ theme, $isSelected }) => 
    $isSelected ? theme.surface2 : theme.text};
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    border-color: ${({ theme }) => theme.accent};
  }
`

export default function SettingsPage() {
  const [language, setLanguage] = React.useState<'ko' | 'en'>('ko')

  return (
    <Container>
      <ProfileSection>
        <ProfileImage>
          <Icon 
            src="/main/profile.png" 
            width={56} 
            height={56}
            fallbackIcon="image"
          />
        </ProfileImage>
        <ProfileInfo>
          <ProfileName>홍길동</ProfileName>
          <ProfileEmail>gildong123@gmail.com</ProfileEmail>
        </ProfileInfo>
        <ChevronIcon />
      </ProfileSection>

      <Section>
        <SectionTitle>다국어 설정 (Language)</SectionTitle>
        <LanguageSection>
          <LanguageButton 
            $isSelected={language === 'ko'}
            onClick={() => setLanguage('ko')}
          >
            한국어
          </LanguageButton>
          <LanguageButton 
            $isSelected={language === 'en'}
            onClick={() => setLanguage('en')}
          >
            영어
          </LanguageButton>
        </LanguageSection>
      </Section>

      <Section>
        <SectionTitle>고객 서비스</SectionTitle>
        <MenuItem>
          <MenuText>이용약관</MenuText>
          <ChevronIcon />
        </MenuItem>
        <MenuItem>
          <MenuText>로그아웃</MenuText>
          <ChevronIcon />
        </MenuItem>
      </Section>
    </Container>
  )
}
