commits.content:
'use client'

import React, { useState } from 'react'
import styled from 'styled-components'
import Modal from '@/components/common/Modal'
import { AppColors } from '@/styles/colors'

type ConfirmButtonProps = {
  title: string
  content: string
  confirmText?: string
  cancelText?: string
  onConfirm?: () => void
  onCancel?: () => void
  width?: number
  buttonLabel?: string
  disabled?: boolean
}

export default function ConfirmButton({
  title,
  content,
  confirmText = '저장',
  cancelText = '취소',
  onConfirm,
  onCancel,
  width = 520,
  buttonLabel = '저장',
  disabled = false,
}: ConfirmButtonProps) {
  const [open, setOpen] = useState(false)

  const handleConfirm = () => {
    setOpen(false)
    onConfirm?.()
  }

  const handleCancel = () => {
    setOpen(false)
    onCancel?.()
  }

  return (
    <>
      <TriggerButton onClick={() => setOpen(true)} disabled={disabled}>
        {buttonLabel}
      </TriggerButton>

      <Modal open={open} title={title} onClose={() => setOpen(false)} width={width} centerTitle>
        <ContentWrapper>
          <Description>{content}</Description>
          <SubText>저장 후에는 이전 정보로 되돌릴 수 없습니다.</SubText>
          <Actions>
            <PrimaryButton onClick={handleConfirm}>{confirmText}</PrimaryButton>
            <SecondaryButton onClick={handleCancel}>{cancelText}</SecondaryButton>
          </Actions>
        </ContentWrapper>
      </Modal>
    </>
  )
}

const TriggerButton = styled.button`
  height: 44px;
  padding: 0 20px;
  border-radius: 8px;
  border: none;
  background: ${AppColors.primary};
  color: #fff;
  font-weight: 600;
  cursor: pointer;

  &:disabled { opacity: .6; cursor: not-allowed; }
`

const ContentWrapper = styled.div`
  padding: 8px 0 8px;
`

const Description = styled.p`
  margin: 12px 0 8px;
  font-size: 16px;
  color: ${AppColors.onSurface};
`

const SubText = styled.p`
  margin: 0 0 28px;
  font-size: 14px;
  color: ${AppColors.onSurfaceVariant};
`

const Actions = styled.div`
  display: flex;
  gap: 16px;
  justify-content: center;
`

const PrimaryButton = styled.button`
  min-width: 140px;
  height: 48px;
  border-radius: 10px;
  border: none;
  background: #2E3040;
  color: #ffffff;
  font-weight: 700;
  font-size: 18px;
  cursor: pointer;
`

const SecondaryButton = styled.button`
  min-width: 140px;
  height: 48px;
  border-radius: 10px;
  border: 1px solid ${AppColors.borderLight};
  background: #ffffff;
  color: ${AppColors.onSurface};
  font-weight: 700;
  font-size: 18px;
  cursor: pointer;
`
