"use client";

import React from 'react';
import styled from 'styled-components';

const MessageWrapper = styled.div`
  display: flex;
  gap: 15px;
  padding: 12px 0;
  max-width: 800px;
`;

const ProfileImage = styled.img`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  object-fit: cover;
`;

const ContentWrapper = styled.div`
  flex: 1;
`;

const Header = styled.div`
  h2 {
    font-size: 18px;
    font-weight: 500;
    line-height: 160%;
    margin: 0 0 5px 0;
    color: ${({ theme }) => theme.text};
  }
`;

const MessageContent = styled.div`
  font-size: 14px;
  line-height: 160%;
  color: ${({ theme }) => theme.text};
  white-space: pre-wrap;

  p {
    margin: 0;
  }
`;

interface AiResponseMessageProps {
  profileImage?: string;
  name?: string;
  content: string | React.ReactNode;
  className?: string;
}

const AiResponseMessage: React.FC<AiResponseMessageProps> = ({
  profileImage = "/ai-estimate/pretty.png",
  name = "AI 컨설턴트",
  content,
  className
}) => {
  return (
    <MessageWrapper className={className}>
      <ProfileImage src={profileImage} alt={name} />
      <ContentWrapper>
        <Header>
          <h2>{name}</h2>
        </Header>
        <MessageContent>
          {typeof content === 'string' ? <p>{content}</p> : content}
        </MessageContent>
      </ContentWrapper>
    </MessageWrapper>
  );
};

export default AiResponseMessage;
