+  transform: translateY(-50%);
+
+  @media (min-width: 1024px) {
+    display: none;
+  }
+`;
+
+const ActionButtonBottom = styled.div`
+  display: none;
+  
+  @media (min-width: 1024px) {
+  width:100%;
+  margin-top:30px;
