'use client'

import React from 'react'
import styled from 'styled-components'
import Link from 'next/link'
import { usePathname } from 'next/navigation'

const FooterWrapper = styled.footer`
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  height: 64px;
  background-color: ${({ theme }) => theme.surface2};
  border-top: 1px solid ${({ theme }) => theme.border};
  z-index: 100;
`

const FooterContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: space-around;
  padding: 0 20px;
`

const NavItem = styled(Link)<{ $isActive?: boolean }>`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
  text-decoration: none;
  color: ${({ theme, $isActive }) => ($isActive ? theme.accent : theme.subtleText)};
  font-size: 12px;

  svg {
    width: 24px;
    height: 24px;
  }
`

const Footer = () => {
  const pathname = usePathname()

  return (
    <FooterWrapper>
      <FooterContent>
        <NavItem href="/ai-estimate" $isActive={pathname === '/ai-estimate'}>
          <span>견적상담</span>
        </NavItem>
        <NavItem href="/my-estimate" $isActive={pathname === '/my-estimate'}>
          <span>나의견적</span>
        </NavItem>
        <NavItem href="/settings" $isActive={pathname === '/settings'}>
          <span>설정</span>
        </NavItem>
        <NavItem href="/fullscreen" $isActive={pathname === '/fullscreen'}>
          <span>전체화면</span>
        </NavItem>
      </FooterContent>
    </FooterWrapper>
  )
}

export default Footer
